import React, { Component } from 'react';
import { ParallaxProvider } from 'react-scroll-parallax';
import { Parallax } from 'react-scroll-parallax';
import ParallaxDisableBoolean from './parallaxAsBoolean';
import ParallaxAsProp from './parallaxAsProps';
import './styles.css';

class HomePage extends Component {

    state = {
		imgSrc:
			'https://images.unsplash.com/photo-1536153592592-47242aa0fc8b?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=ce3d02858565966a3bab86db4ab40f4c&auto=format&fit=crop&w=2551&q=80',
		width: window.innerWidth,
	};

	componentDidMount() {
		window.addEventListener('resize', this.handleWindowSizeChange);
	}

	// make sure to remove the listener
	// when the component is not mounted anymore
	componentWillUnmount() {
		window.removeEventListener('resize', this.handleWindowSizeChange);
	}
	// dinamically detect window
	handleWindowSizeChange = () => {
		this.setState({ width: window.innerWidth });
	};

	setOffsetYMax(windowWidth) {
		return windowWidth <= 480 ? 0 : 10;
	}

	setOffsetYMin(windowWidth) {
		return windowWidth <= 480 ? 0 : -120;
	}

	disableParallax(windowWidth) {
		return windowWidth <= 480;
    }

    render() {
        return (
            <ParallaxProvider>
                	<div className="background">
					<h1>Disable React-Scroll-Parallax on mobile</h1>
					<h3>(resize the window to see it in action)</h3>
					<Parallax
						className="custom-class"
						offsetYMax={this.setOffsetYMax(this.state.width)}
						offsetYMin={this.setOffsetYMin(this.state.width)}
						slowerScrollRate
						tag="figure">
						<img src={this.state.imgSrc} alt="image description" />
						<h3>With Methods</h3>
					</Parallax>

					<ParallaxAsProp
						minY={this.setOffsetYMin(this.state.width)}
						maxY={this.setOffsetYMax(this.state.width)}
						img={this.state.imgSrc}
					/>

					<ParallaxDisableBoolean
						isDisabled={this.disableParallax(this.state.width)}
						img={this.state.imgSrc}
					/>
				</div>
            </ParallaxProvider>
        )
    }   
}


export default HomePage;
